# Install with pip install firecrawl-py
from firecrawl import FirecrawlApp
import os

app = FirecrawlApp(api_key=os.environ.get('FIRECRAWL_API_KEY', 'YOUR_API_KEY'))

response = app.scrape_url(url='google.com/', params={
	'formats': [ 'markdown' ],
})

print(response)
